import { useState } from "react";

import logo from './logo.svg';
import './App.css';
import Task from "./components/Task";


const initialTasks = [
  { 
    id: 1, 
    content: "買い物", 
    isDone: false,},
  { id: 2, content: "ランニング"},
  { id: 3, content: "プログラミング学習"},
]
function App() {
  const [tasks, setTasks] = useState(initialTasks);

