  }
  return (
    <Box mt="64px">
      <Center>
        <Box>
          <Box mb="24px">
            <Text fontSize="24px" as="b">
              タスク一覧
            </Text>
          </Box>
          <CheckboxGroup>
            {tasks.map(task => {
              return (
                <Task key={task.id} name={task.content} />
                )
              })}
          </CheckboxGroup>
        </Box>
      </Center>
    </Box>
  );
}

export default App;
